<?phpclass larajobs extends api{    //Constructor    public function __construct(){        error_reporting(E_ALL);        // set display errors status        ini_set('display_errors', 0); // 1-turn on all error reporings 0-turn off all error reporings        // change max execution time to unlimitied        ini_set('max_execution_time', 0);        //Context for file_get_content        $this->context = stream_context_create(array(            'http'=>array(                'method'=>"GET",                'timeout' => 3600,                'header'=> "User-Agent: Mozilla/5.0 (Windows NT 5.1; rv:31.0) Gecko/20100101 Firefox/31.0\r\n "            )        ));    }    //Extract Email Address	function extractEmailAddress($string){		$matches = array();		$pattern = '/[A-Za-z0-9_-]+@[A-Za-z0-9_-]+\.([A-Za-z0-9_-][A-Za-z0-9_]+)/';		preg_match_all($pattern,$string,$matches);        return $matches[0][0];    }    //Execute    public function execute($hermesEndpoint){        global $afx;		//Define the $baseUrl		$baseUrl = "https://larajobs.com";        $this->json_response = array();		//Go get the list of jobs        $jobList = json_decode(file_get_contents("{$baseUrl}/api/jobs"),true);        //Make sure we got something        if(sizeof($jobList) > 0){            //Loop            foreach($jobList as $currentJob){                //Only interested in remote gigs                if($currentJob["location_remote"] == "1"){                    //Build the $jobData                    $jobData["title"] = $currentJob["title"];                    $jobData["payload"] = $afx->action_value;                    $jobData["url"] = "{$baseUrl}/job/{$currentJob['id']}";                    $jobData["employer"] = $currentJob["user"]["company_name"];                    $jobData["description"] = nl2br($currentJob["description"]);                    $jobData["response_email"] = $this->extractEmailAddress($currentJob["raw_how_reply"]);                    //Make sure we have a response_email                    if(strlen($jobData["response_email"]) > 0){                        //Create a $curlHandle                        $curlHandle = curl_init($hermesEndpoint);                        //Initialize $curlHandle                        curl_setopt($curlHandle, CURLOPT_POST, true);                        curl_setopt($curlHandle,CURLOPT_RETURNTRANSFER, true);                        curl_setopt($curlHandle, CURLOPT_POSTFIELDS, $jobData);                                                                    $curlResult = json_decode(curl_exec($curlHandle),true);                        //Make sure things went as well as we could've hoped                        if($curlResult == true){                            print_r($jobData);                            $this->json_response[] = $jobData;                        }                        else{                            print_r($curlResult);                        }                                        }                }            }        }    }}?>