<?phpclass thirtysevensignals extends api{    //Constructor    public function __construct(){        error_reporting(0);        // set display errors status        ini_set('display_errors', 0); // 1-turn on all error reporings 0-turn off all error reporings        // change max execution time to unlimitied        ini_set('max_execution_time', 0);    }    //Execute    public function execute($hermesEndpoint){        global $afx;        $this->json_response = array();        $pageObject = str_get_html(file_get_contents("https://weworkremotely.com/jobs/search?term=web"));        //Loop through the list of jobs        foreach($pageObject->find("#category-2 li") as $currentJob){            $jobUrl = "https://weworkremotely.com".$currentJob->find("a",0)->href;            //Get the $jobObject            $jobObject = str_get_html(file_get_contents($jobUrl));            //Set the $jobData            $jobData["url"] = $jobUrl;            $jobData["employer"] = $jobObject->find(".company",0)->plaintext;                        $jobData["title"] = $jobObject->find(".listing-header-container h1",0)->plaintext;            $jobData["description"] = trim($jobObject->find(".listing-container",0)->innertext);                                    $emailAddresses = array();            preg_match('/[A-Za-z0-9_-]+@[A-Za-z0-9_-]+\.([A-Za-z0-9_-][A-Za-z0-9_]+)/',$jobObject->find(".apply a",0)->plaintext,$emailAddresses);            $jobData["response_email"] = $emailAddresses[0];            //Make sure we have a response_email                        if(isset($jobData["response_email"])){                //Create a $curlHandle                $curlHandle = curl_init($hermesEndpoint);                //Initialize $curlHandle                curl_setopt($curlHandle, CURLOPT_POST, true);                curl_setopt($curlHandle,CURLOPT_RETURNTRANSFER, true);                curl_setopt($curlHandle, CURLOPT_POSTFIELDS, $jobData);                                                            $curlResult = json_decode(curl_exec($curlHandle),true);                //Make sure things went as well as we could've hoped                if($curlResult == true){                    print_r($jobData);                    $this->json_response[] = $jobData;                }                else{                    print_r($curlResult);                }             }                        }        //Send the JSON Response        $this->send_json_response();    }}?>